import React from 'react';
import data from './data.js'
import QuestionList from './containers/QuestionList'


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      questions: [],
      guesses: new Array(5)
    }
  }

  componentDidMount() {
    this.handleStart()
  }

  handleStart = () => {
    let selectedQs = []
    let num
    for (let i = 0; i < 5; i++) {
      num = Math.floor(Math.random() * data.questions.length)
      selectedQs.push(data.questions[num])
    }
    this.setState({ questions: selectedQs })
  }

  render() {
    return (
      <div>
        <QuestionList questions={this.state.questions} guesses={this.state.guesses} />
      </div>
    )
  }
}

export default App;
