import React from 'react';
import data from './data.js'
import QuestionList from './containers/QuestionList'


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      questions: [],
      guesses: new Array(5)
    }
  }

  handleStart = () => {
    let selectedQs
    for (let i = 0; i < 5; i++) {
      selectedQs.push(data[Math.floor(Math.random() * data.length)])
    }
    console.log(selectedQs)
  }

  render() {
    console.log(this.state)
    return (
      <div>
        <QuestionList />
      </div>
    )
  }
}

export default App;
