import React from 'react';
import data from './data.js'
import QuestionList from './containers/QuestionList'


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      questions: [],
      guesses: new Array(5)
    }
  }

  componentDidMount() {
    this.handleStart()
  }

  handleStart = () => {
    let selectedQs = []
    console.log('data: ', data.questions[1])
    let num
    for (let i = 0; i < 5; i++) {
      num = Math.floor(Math.random() * data.length)
      console.log('N = ', num)
      console.log('this question: !!!', data.questions[num])
      selectedQs.push(data.questions[num])
    }
    this.setState({ questions: selectedQs })
  }

  render() {
    console.log(this.state)
    return (
      <div>
        <QuestionList />
      </div>
    )
  }
}

export default App;
